import React,{useEffect,useState} from 'react'
import { Row,Col,ListGroup,Image,Card,Button } from 'react-bootstrap'
import Loader from "../components/Loader"
import { PayPalButton } from 'react-paypal-button-v2'
import Message from "../components/Message"
import { useDispatch,useSelector } from 'react-redux'
import { useNavigate,Link,useParams } from "react-router-dom"
import { getOrderDetails, payOrder } from '../components/actions/orderActions'
import { ORDER_PAY_RESET } from '../components/constants/orderConstants'



export default function OrdersPage() {
    const { id } = useParams();
    const orderId = id
    
    const dispatch = useDispatch()
    const [sdkReady, setSdkReady] = useState(false)
    const orderDetails = useSelector(state => state.orderDetails)
    const {order, error, loading} = orderDetails
    const orderPay = useSelector(state => state.orderPay)
    const {loading:loadingPay,success:successPay} = orderPay
    const navigate = useNavigate()
    if(!loading && !error){
       
    }

    //Razorpay Payment

    const options = {
        key: 'ABaUfIyaUqtv47F2JWziqkEI',
        amount:(order.itemsPrice)*100, 
        name: 'Acme shop',
        description: 'some description',
        image: 'https://cdn.razorpay.com/logos/7K3b6d18wHwKzL_medium.png',
        handler: function(response) {
            alert(response.razorpay_payment_id);
        },
        prefill: {
            name: 'Ajith Kumar',
            contact: '6369600227',
            email: 'nexusrobotech2022@gmail.com'
        },
        notes: {
            address: '3/199, Anumandhanoor village, Thokkiyam Post, Thirupathur Tk&Dt - 635901'
        },
        theme: {
            color: 'blue',
            hide_topbar: false
        }
    };

    //Razorpay Scripts

    const razorPayScript = ()=>{
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.async = true;
        script.onload = ()=>{
            setSdkReady(true)
        }
        document.body.appendChild(script);
    }
    
    


    //Paypal Scripts
     const addPayPalScript = () =>{
        const script = document.createElement('script')
        script.type = 'text/javascript'
        script.src = "https://www.paypal.com/sdk/js?client-id=AYarabvQywJ6NpmHVyF0ffUP0Go1OgtcIn5nAYN_KRkEUVbYf6QeVml3pE2XTQ2NiwVBiQceZBU_VsF-"
        script.async = true
        script.onload = ()=>{
            setSdkReady(true)
        }
        document.body.appendChild(script)
     }

     //Paypal scripts 
    
    useEffect(()=>{
        if(!order || successPay || order._id !==Number(orderId)){
            dispatch({type:ORDER_PAY_RESET})
            dispatch(getOrderDetails(orderId))
        }else if(!order.isPaid){
            if(!window.paypal){
                addPayPalScript()
                razorPayScript()
            }else{
                setSdkReady(true)
            }
        }
        
         
    },[dispatch,order,orderId, successPay,])

    const displayRazorpay = () => {
        var razorpay = new window.Razorpay(options);
        razorpay.open();
    
    const successpaymentHandler = (paymentResult) =>{
        dispatch(payOrder(orderId, paymentResult))

    }
  return (

    loading ? (<Loader/>) : error ? (<Message variant='danger'>{error}</Message>) :(
    <div>
        <h1>Order:{order._id}</h1>
    <Row>
        <Col md={8}>
            <ListGroup variant='flush'>
                <ListGroup.Item>
                    <h2>Shipping</h2>
                    <p><strong>Name:</strong>{order.user.name}</p>
                    <p><strong>Email:</strong><a href={`mailto:${order.user.email}`}>{order.user.email}</a></p>
                    <p>
                        <strong>Shipping: </strong>
                        {order.shippingAddress.address},{order.shippingAddress.city},
                        {''}
                        {order.shippingAddress.postalcode},
                        {''}
                        {order.shippingAddress.country}
                    </p>
                    {order.isDelivered ? (<Message variant='success'>Delivered On {order.deliveredAt}</Message>
                     ):(<Message variant='warning'>Not Delivered</Message>)}
                </ListGroup.Item>

                <ListGroup.Item>
                    <h2>Payment Method</h2>
                    <p>
                        <strong>Payment Through: </strong>
                        {order.paymentMethod}
                        </p>
                     {order.isPaid ? (<Message variant='success'>Paid on {(order.paidAt).toLocaleString()}</Message>
                     ):(<Message variant='warning'>Not Paid</Message>)}

                </ListGroup.Item>

                <ListGroup.Item>
                    <h2>Order Items</h2>
                    {order.orderItems.length == 0 ? (<Message>
                        Your order is empty
                    </Message>)
                    :(<ListGroup variant='flush'>
                        {order.orderItems.map((item,index) =>(
                            <ListGroup.Item key={index}>
                                <Row>
                                    <Col md={1}>
                                    <Image src={item.image} alt={item.name} fluid rounded/>
                                    </Col>
                                    <Col>
                                     <Link to={`/product/${item.product}`}>{item.name}</Link>
                                    </Col>

                                    <Col md={4}>
                                        {item.qty} X Rs.{item.price} = Rs.{(item.qty * item.price).toFixed(2)}
                                    </Col>



                                </Row>

                            </ListGroup.Item>
                        ))}

                    </ListGroup>)}
                    


                </ListGroup.Item>

            </ListGroup>
        </Col>

        <Col md={4}>
            <Card>
                <ListGroup variant='flush'>
                    <ListGroup.Item>
                        <h2>Order Summary</h2>
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <Row>
                            <Col>Items:</Col>
                            <Col>Rs.{order.itemsPrice}</Col>
                        </Row>
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <Row>
                            <Col>Shipping:</Col>
                            <Col>Rs.{order.shippingPrice}</Col>
                        </Row>
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <Row>
                            <Col>Tax:</Col>
                            <Col>Rs.{order.taxPrice}</Col>
                        </Row>
                    </ListGroup.Item>

                    <ListGroup.Item>
                        <Row>
                            <Col>Total:</Col>
                            <Col>Rs.{order.totalPrice}</Col>
                        </Row>
                    </ListGroup.Item>
                    {(() => {
                                if (!order.isPaid && order.paymentMethod === 'Paypal') {
                                return (
                                    <PayPalButton
                                amount = {order.totalPrice}
                                onSuccess={successpaymentHandler}
                                
                                />
                                )
                                } else if (!order.isPaid && order.paymentMethod === 'Razorpay') {
                                return (
                                    <Button className='btn-md'
                                    
                                    onClick={displayRazorpay}>Pay with Razorpay</Button>
                                )
                                } else {
                                return (
                                    <div>
                                        <p className='codtext'>Cash On Delivery</p>
                                    </div>
                                )
                                }
                            })()}

                </ListGroup>

            </Card>
        </Col>
    </Row>
    
    
    
    </div>
  ))
}
}
